// React Chat App UI with Join Room Flow and Socket Integration

import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [isJoined, setIsJoined] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [participants, setParticipants] = useState([]);

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    socket.on('user_list', (userList) => {
      setParticipants(userList);
    });

    return () => {
      socket.off('receive_message');
      socket.off('user_list');
    };
  }, []);

  const joinRoom = () => {
    if (username.trim() && room.trim()) {
      socket.emit('join_room', { username, room });
      setIsJoined(true);
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit('send_message', { room, sender: username, message });
      setMessage('');
    }
  };

  if (!isJoined) {
    return (
      <div className="h-screen flex flex-col justify-center items-center bg-gray-100">
        <h2 className="text-2xl font-bold mb-4">Join a Chat Room</h2>
        <input
          className="p-2 m-2 border"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className="p-2 m-2 border"
          placeholder="Room Name"
          value={room}
          onChange={(e) => setRoom(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={joinRoom}
        >
          Join Room
        </button>
      </div>
    );
  }

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      <div className="bg-blue-600 text-white p-4 flex justify-between">
        <h2 className="text-lg font-semibold">Room: {room}</h2>
        <span>Users: {participants.join(', ')}</span>
      </div>

      <div className="flex-1 overflow-y-auto p-4">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 p-2 rounded-lg shadow max-w-md ${msg.sender === username ? 'ml-auto bg-blue-100' : 'bg-white'}`}
          >
            <div className="text-sm font-bold text-blue-700">{msg.sender}</div>
            <div>{msg.message}</div>
          </div>
        ))}
      </div>

      <div className="flex p-4 border-t">
        <input
          className="flex-1 border rounded px-3 py-2"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Type your message..."
        />
        <button
          className="ml-2 bg-blue-500 text-white px-4 py-2 rounded"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
