// Final version with your custom dashboard + fixed logic integration

import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

function ChatApp() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [isJoined, setIsJoined] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [showSidebar, setShowSidebar] = useState(false);

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    socket.on('user_list', (userList) => {
      setParticipants(userList);
    });

    return () => {
      socket.off('receive_message');
      socket.off('user_list');
    };
  }, []);

  const joinRoom = () => {
    if (username.trim() && room.trim()) {
      socket.emit('join_room', { username, room });
      setIsJoined(true);
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit('send_message', { room, sender: username, message });
      setMessage('');
    }
  };

  const leaveChat = () => {
    window.location.reload();
  };

  if (!isJoined) {
    return (
      <div className="h-screen flex flex-col justify-center items-center bg-gradient-to-tr from-purple-600 to-indigo-600 text-white">
        <h2 className="text-3xl font-bold mb-6">FlashTalk Chat</h2>
        <input
          className="p-2 m-2 w-64 rounded text-black"
          placeholder="Your Name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className="p-2 m-2 w-64 rounded text-black"
          placeholder="Room Name"
          value={room}
          onChange={(e) => setRoom(e.target.value)}
        />
        <button
          className="bg-white text-purple-600 px-6 py-2 mt-4 rounded shadow"
          onClick={joinRoom}
        >
          Join Chat
        </button>
      </div>
    );
  }

  return (
    <div className="relative flex flex-col h-screen bg-gray-50">
      <div className="flex justify-between items-center bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-6 py-4">
        <h2 className="text-xl font-bold">Room: {room}</h2>
        <button
          className="bg-white text-purple-600 font-semibold py-1 px-3 rounded shadow"
          onClick={() => setShowSidebar(!showSidebar)}
        >
          ☰ Menu
        </button>
      </div>

      <div className="flex flex-1 overflow-hidden">
        {/* Chat messages */}
        <div className="flex-1 p-4 overflow-y-auto flex flex-col">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`mb-3 p-3 rounded-lg shadow max-w-md text-white ${msg.sender === username ? 'ml-auto bg-gray-800' : 'bg-gray-600'}`}
            >
              <div className="font-semibold text-sm text-purple-300">{msg.sender}</div>
              <div className="break-words">{msg.message}</div>
            </div>
          ))}
        </div>

        {/* Sidebar for participants + leave */}
        <div
          className={`fixed top-0 right-0 h-full w-64 bg-white border-l p-4 shadow-md transform transition-transform duration-300 ease-in-out ${showSidebar ? 'translate-x-0' : 'translate-x-full'}`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3 className="font-bold text-lg">Participants</h3>
            <button onClick={() => setShowSidebar(false)} className="text-xl">×</button>
          </div>
          <ul className="space-y-2 overflow-y-auto h-[calc(100vh-200px)]">
            {participants.map((p, i) => (
              <li key={i} className="flex items-center gap-2 text-sm">
                <span className="w-2 h-2 bg-green-500 rounded-full"></span>
                {p === username ? `${p} (You)` : p}
              </li>
            ))}
          </ul>
          <button
            className="mt-6 bg-red-500 text-white px-4 py-2 rounded w-full"
            onClick={leaveChat}
          >
            Leave Chat
          </button>
        </div>
      </div>

      <div className="flex p-4 bg-white border-t items-center">
        <input
          className="flex-1 border border-gray-300 rounded-full px-4 py-2 text-sm mr-2 focus:outline-none focus:ring"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Type your message..."
        />
        <button
          className="bg-gradient-to-r from-indigo-400 to-purple-500 text-white px-4 py-2 rounded-full shadow"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatApp;
